    private void btnActualizarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnActualizarActionPerformed
        // TODO add your handling code here:
        marcaSeleccionada.setNombreMarca(txtNombreMarca.getText());
        base.modificarMarca(marcaSeleccionada);
        cargarModeloMarca();
        this.btnActualizar.setEnabled(false);
        this.btnEliminar.setEnabled(false);
    }//GEN-LAST:event_btnActualizarActionPerformed

    private void btnEliminarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnEliminarActionPerformed
        // TODO add your handling code here:
       if( base.eliminarMarca(marcaSeleccionada)>0){
            System.out.println("ok...");
       }else{
            System.out.println("No se puede eliminar el primer registro...");
       }
        cargarModeloMarca();
        this.btnActualizar.setEnabled(false);
        this.btnEliminar.setEnabled(false);
    }//GEN-LAST:event_btnEliminarActionPerformed

    private void txtNombreMarcaKeyReleased(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_txtNombreMarcaKeyReleased
        // TODO add your handling code here:
         ArrayList<Marcas> lista = new ArrayList<Marcas>();
        String nombre = this.txtNombreMarca.getText();
        lista = base.obtenerMarca(nombre);

        int numFila = lista.size();

        modeloTabla.setNumRows(numFila);
        for (int i = 0; i < numFila; i++) {
            Marcas mar = lista.get(i);

            int codigo = mar.getIdMarca();
            String nombreCat = mar.getNombreMarca();
            modeloTabla.setValueAt(codigo, i, 0);
            modeloTabla.setValueAt(mar, i, 1);
        }
    }//GEN-LAST:event_txtNombreMarcaKeyReleased

