    reportesDatos objReporte = new reportesDatos();
    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed

        if (radioProductoStock.isSelected()) {
            objReporte.imprimirAlertaProductos();
        } else {
            try {
                Calendar cal;
                int d, m, a;
                cal = txtFecha.getCalendar();
                d = cal.get(Calendar.DAY_OF_MONTH);
                m = cal.get(Calendar.MONTH);
                a = cal.get(Calendar.YEAR) - 1900;
                String fecha = String.valueOf(new Date(a, m, d));

                if (radioProductoMas.isSelected()) {
                    objReporte.imprimirProductosVendidos(fecha);
                } else if (radioProductoGanancia.isSelected()) {
                    objReporte.imprimirGananciasProductos(fecha);
                }
            } catch (Exception e) {
                JOptionPane.showMessageDialog(null, "Las fechas deben ser v√°lidas");
            }
        }
    }//GEN-LAST:event_jButton1ActionPerformed


